import React, { useState} from "react";
import Modal from 'react-bootstrap/Modal';

export default function ImagePreview({ nasaPicture, title, description }) {
  const [show, setShow] = useState(false);
  return (
    <main className="grid">
      <div className="responsive">  
        <div className="gallery">       
          <img src={nasaPicture} alt={title} className="Img_Grid" onClick={() => setShow(true)}/>
          <div className="desc">{title}</div> 
        </div>
      </div>
    
      <Modal
        show={show}
        onHide={() => setShow(false)}
        dialogClassName="modal-110w"
        aria-labelledby="example-custom-modal-styling-title"
        className="Modal"
      >
        <img src={nasaPicture} alt={title} />
        <div className="container-Modal">
          <h1 className="Title_Modal">{title}</h1>
          <p className="description_Modal">{description}</p>
        </div>
      </Modal>
    </main>
  );
}
