import React from "react";
import styled from "styled-components";
import { useState } from "react";
import "../../App.css";
import "../../styles/home.css";
import { makeStyles } from "@material-ui/core/styles";
import { Button, TextField } from "@material-ui/core";
import NavBar from "../components/NavBar";
import Earth_Background from "../../img/Earth_Background.mp4";
import ImagePreview from "./ImagePreview";

const useStyles = makeStyles((theme) => ({
  root: {
    "& > *": {
      margin: theme.spacing(1),
    },
  },
  search: {
    width: 300,
  },
}));

export default function HomePage({ items }) {
  const [search, setSearch] = useState("");
  const [photos, setPhotos] = useState(items);
  const classes = useStyles();

  return (
    <>
      <div className="Home">
        <video class="Video_Background" autoPlay loop muted>
          <source src={Earth_Background} type="video/mp4" />
        </video>
        <Home>
          <NavBar />
          <Body></Body>
          <Jumbotron>
            <h1 class="TitleHome">Olá, Bem vindo(a) a Galeria da NASA!</h1>
            <p class="PHome">
              Aqui você encontra fotos dos planetas e galáxias!
            </p>
            <TextField
              className={classes.search}
              id="nasaSearch"
              label="Search for an image"
              variant="outlined"
              onChange={(e) => setSearch(e.target.value)}
            />
            <Button
              variant="contained"
              color="primary"
              disabled={search === ""}
              onClick={async () => {
                const results = await fetch(
                  `https://images-api.nasa.gov/search?media_type=image&q=${search}`
                );
                const previews = await results.json();
                setPhotos(await previews.collection.items);
              }}
            >
              BUSCA
            </Button>
            <div>
              <div>
                {photos &&
                  photos.map((preview) => (
                    <ImagePreview
                      key={preview.data[0].title}
                      nasaPicture={preview.links[0].href}
                      title={preview.data[0].title}
                      description={preview.data[0].description}
                    />
                  ))}
              </div>
            </div>
          </Jumbotron>
        </Home>
      </div>
    </>
  );
}

const Home = styled.div`
  width: 100vw;
  height: 100vh;
  color: white;
`;
const Body = styled.div`
  margin-top: 50px;
  margin-right: 100px;
`;

const Jumbotron = styled.div`
  margin-top: 2%;
`;
