import React, { useState } from "react";
import { Modal, Grid, Typography } from "@material-ui/core";

export default function ImagePreview({ nasaPicture, title, description }) {
  const [show, setShow] = useState(false);

  return (
    <Grid item xs={12} sm={6} md={4} lg={3}>
      <div className="gallery">
        <img
          src={nasaPicture}
          className="Img_Grid"
          onClick={() => setShow(true)}
        />
        <div className="desc">{title}</div>
      </div>

      <Modal open={show} onClose={() => setShow(false)}>
        <div className="Modal">
          <img src={nasaPicture} />
          <div className="container-Modal">
            <Typography variant="h4" className="Title_Modal">
              {title}
            </Typography>
            <Typography variant="body1" className="description_Modal">
              {description}
            </Typography>
          </div>
        </div>
      </Modal>
    </Grid>
  );
}
